import React, { useState } from 'react';
import MyTable from '../src/table';
import '../src/index.css';
import { CSSTransition } from 'react-transition-group';
// Somewhere in your React tree:

function App() {
  return (
    <div className="App">
      <Navbar>
        <NavImage></NavImage>
        <Images></Images>
        <NavText text="Angelena Doe"></NavText>

        <NavItem icon="🔲"></NavItem>
        <NavItem icon="🔲"></NavItem>
        <NavItem icon="🔲"></NavItem>
        <NavItem icon="🔲"></NavItem>
      </Navbar>
      <SideBar>
        <SideImage image />
        <SideText text="Angelena Doe" text1="Project Manager"></SideText>
        <SideBtn button=" Add Project">
          <DropdownMenu />
        </SideBtn>
      </SideBar>
      <Cardshow />
      {/* <div className="boxed">
        <MyTitle titlea />
        <div className="flex-container">
          {' '}
          <div className="myDIV">
            <div id="card-container">{'Stock Total'}</div>
            &emsp; &emsp;<div id="card-container">{'$15000'}</div>
            <div id="card-increase">{'Increase by 60%'}</div>
            <div id="cir2"></div>
            <div id="cir3"></div>
          </div>
          <div className="myDIV1">
            <div id="card-container">{'Total Profit'}</div>
            &emsp; &emsp;
            <div id="card-container">
              {'$25000'}
              <div id="card-increase">{'Increase by 30%'}</div>
              <div id="cir4"></div>
              <div id="cir5"></div>
            </div>
          </div>
          <div className="myDIV2">
            {' '}
            <div id="card-container">{'Unique Visitors'}</div>
            &emsp; &emsp;<div id="card-container">{'25000'}</div>
            <div id="card-increase">{'Increase by 80%'}</div>
            <div className="title"></div>
            <div id="cir1"></div>
            <div id="cir"></div>
          </div>
        </div>
      </div>
      <MyIcon> </MyIcon>
      <MyIconrec white></MyIconrec>
      <Iconrec rec1 />
      <Iconrec1 rec2 />
      <Iconrec2 rec3 /> */}

      <MyTable />
    </div>
  );
}

function MyTitle(props) {
  return <div className="titleDash">Dashboard{props.titlea}</div>;
}
function MyIcon(props) {
  return <div id="myDIVicon">{props.child}</div>;
}

function MyIconrec(props) {
  return <div id="myDIViconrec">{props.white}</div>;
}
function Iconrec(props) {
  return <div id="iconrec">{props.rec1}</div>;
}
function Iconrec1(props) {
  return <div id="iconrec1">{props.rec2}</div>;
}
function Iconrec2(props) {
  return <div id="iconrec2">{props.rec3}</div>;
}

function Navbar(props) {
  return (
    <nav className="navbar">
      <ul className="navbar-nav">{props.children}</ul>
    </nav>
  );
}
function SideBar(props) {
  return (
    <nav className="side-bar">
      <div className="sidebar-nav">{props.children}</div>
    </nav>
  );
}
function NavImage(props) {
  return (
    <a href="#" className="image">
      {props.image}
    </a>
  );
}

function Images(props) {
  return (
    <a href="#" className="image-keev">
      {props.keev}
    </a>
  );
}
function SideImage(props) {
  return (
    <a href="#" className="sideimage">
      {props.image}
    </a>
  );
}
function NavText(props) {
  return (
    <a href="#" id="text">
      {props.text}
    </a>
  );
}
function SideText(props) {
  return (
    <div>
      <a href="#" id="sidetext">
        {props.text}
      </a>
      <a href="#" id="sidetext1">
        {props.text1}
      </a>
      <a href="#" id="sidetext2">
        {props.text2}
      </a>
    </div>
  );
}

function DropdownMenu() {
  function DropdownItem(props) {
    return (
      <div>
        <a href="#" className="menu-item">
          {props.children}
        </a>
      </div>
    );
  }

  return (
    <div className="dropdown">
      <div className="menu">
        <DropdownItem>Dashboard</DropdownItem>
        <DropdownItem>UI Elements</DropdownItem>
        <DropdownItem>Components</DropdownItem>
        <DropdownItem>Form Stuff</DropdownItem>
        <DropdownItem>Data Table</DropdownItem>
        <DropdownItem>Icons</DropdownItem>
        <DropdownItem>Sample Page</DropdownItem>
        <DropdownItem>Extra</DropdownItem>
      </div>
    </div>
  );
}
function SideBtn(props) {
  const [open, setOpen] = useState(false);

  return (
    <div>
      {' '}
      <a href="#" className="btn" onClick={() => setOpen(!open)}>
        {props.button}
      </a>
      {open && props.children}
    </div>
  );
}

function Cardshow() {
  const [cardState, changeState] = useState({
    activeObject: null,
    objects: [{ id: 1 }, { id: 2 }, { id: 3 }],
  });
  function toggleActive(index) {
    changeState({ ...cardState, activeObject: cardState.objects[index] });
  }

  function toggleActiveStyles(index) {
    if (cardState.objects[index] === cardState.activeObject) {
      return 'myDIV myDIV1';
    } else {
      return 'myDIV1 myDIV2';
    }
  }
  return (
    <div className="myDIV">
      {cardState.objects.map((elements, index) => (
        <div
          key={index}
          className={toggleActiveStyles(index)}
          onClick={() => {
            toggleActive(index);
          }}
        ></div>
      ))}
    </div>
  );
}

function NavItem(props) {
  return (
    <li className="nav-item">
      <a href="#" className="icon-button">
        {props.icon}
      </a>
    </li>
  );
}

export default App;
